const texto = $json.messages[0].text.body?.toLowerCase().trim() || '';
const numero = $json.messages[0].from;

const saudacoes = ['oi', 'olá', 'ola', 'bom dia', 'boa tarde', 'boa noite'];
let etapa = null;

// Se for saudação
if (saudacoes.includes(texto)) {
  etapa = 'menu_principal';
}
// Se for "status"
else if (texto.includes('status')) {
  etapa = 'aguardando_projeto_id';
}
// Se for "análise" ou "analise"
else if (texto.includes('análise') || texto.includes('analise')) {
  etapa = 'modo_analitico';
}
// Se for número (ID do projeto)
else if (!isNaN(texto) && texto.length <= 10) {
  etapa = 'validando_projeto_id';
}
// Se já estiver aguardando pergunta analítica, continua nessa etapa
else {
  etapa = 'aguardando_pergunta_analitica';
}

return [{
  json: {
    texto,
    numero,
    etapa
  }
}];

const texto = $json.texto?.trim().toLowerCase() || '';
const numero = $json.numero || '';
let etapa = $json.etapa || null;

// 🚪 Comando para sair
if (texto === 'sair') {
  return [{
    json: {
      etapa: 'sair',
      numero,
      mensagem: '👋 Conversa encerrada. Diga "oi" para começar novamente quando quiser.'
    }
  }];
}

// 👋 Início da conversa ou reset
if (!etapa || texto === 'oi') {
  return [{
    json: {
      etapa: 'menu_principal',
      numero,
      mensagem: `🤖 Olá! Como posso te ajudar hoje?\n\n❇️ Digite *"status"* para ver um projeto específico\n❇️ Digite *"análise geral"* para ver insights e recomendações.`
    }
  }];
}

// 📌 Menu principal
if (etapa === 'menu_principal') {
  if (texto.includes('status')) {
    return [{
      json: {
        etapa: 'aguardando_projeto_id',
        numero,
        mensagem: '🗂️ Certo! Por favor, digite o ID do projeto que você quer consultar.'
      }
    }];
  }

  if (texto.includes('análise') || texto.includes('analise')) {
    return [{
      json: {
        etapa: 'aguardando_pergunta_analitica',
        numero,
        mensagem: `🤖 Agora estou pronto para te ajudar com a análise dos projetos!\n\nVocê pode me perguntar, por exemplo:\n🔹 "Qual projeto está parado?"\n🔹 "O projeto 941 está atrasado?"\n🔹 "Quais são os estratégicos?"\n🔹 "Me dá um resumo geral"\n\n💬 Escreva com suas palavras! Estou pronto pra responder.`
      }
    }];
  }
}

// 🔢 ID do projeto
if (etapa === 'aguardando_projeto_id') {
  if (!isNaN(texto) && texto !== '') {
    return [{
      json: {
        etapa: 'validando_projeto_id',
        numero,
        projeto_id: texto,
        mensagem: `🔍 Verificando dados do projeto ID: *${texto}*...`
      }
    }];
  } else {
    return [{
      json: {
        etapa: 'aguardando_projeto_id',
        numero,
        mensagem: '⚠️ Por favor, digite apenas o número (ID) do projeto.'
      }
    }];
  }
}

// 🧠 Etapa aguardando pergunta analítica
if (etapa === 'aguardando_pergunta_analitica') {
  return [{
    json: {
      etapa: 'interpretando_pergunta_analitica',
      numero,
      pergunta: texto,
      mensagem: null
    }
  }];
}

// 🧭 Fallback
return [{
  json: {
    etapa,
    numero,
    mensagem: '🤔 Desculpe, não entendi. Poderia tentar novamente? Se precisar, digite *"oi"* para recomeçar ou *"sair"* para encerrar.'
  }
}];

const numero = $node["Gerenciar Etapa da Conversa"].json.numero || "";

// Acesso correto à lista de projetos
const projetos = items[0].json.data.data || [];

let texto = "🤖 Tudo bem! Qual projeto deseja consultar?\n\n";

// Montar lista com card_id e título
projetos.forEach(proj => {
  texto += `🆔 ${proj.card_id} - ${proj.title}\n`;
});

texto += "\n*Responda com o número do ID do projeto.*";

return [
  {
    json: {
      etapa: "aguardando_projeto_id",
      numero,
      mensagem: texto
    }
  }
];

const projeto = $input.first().json.data.data?.[0] || {};
const resumoBruto = projeto.custom_fields?.[0]?.value || "";

// Função para limpar HTML
function limparHtml(texto) {
  return texto.replace(/<[^>]*>/g, '').trim();
}

// Função para extrair conteúdo após cada título
function extrairResumo(resumo, tag) {
  const regex = new RegExp(`# ${tag}\\?\\s*([^#]*)`, 'i');
  const match = resumo.match(regex);
  return match ? match[1].trim() : "(sem info)";
}

// Limpa o HTML e prepara o texto
const resumoLimpo = limparHtml(resumoBruto);

// Extrai cada ponto do 5W2H
const oQue = extrairResumo(resumoLimpo, "O que");
const porQue = extrairResumo(resumoLimpo, "Por que");
const onde = extrairResumo(resumoLimpo, "Onde");
const quando = extrairResumo(resumoLimpo, "Quando");
const quem = extrairResumo(resumoLimpo, "Quem");
const como = extrairResumo(resumoLimpo, "Como");
const quanto = extrairResumo(resumoLimpo, "Quanto");

// Monta a mensagem final
const mensagem = 
` *Status do Projeto - ${projeto.title || "(sem título)"}*\n\n` +
` *Objetivo:* ${(projeto.description || "").replace(/<[^>]*>/g, '').trim() || "(sem descrição)"}\n` +
` *Período Previsto:* ${projeto.initiative_details?.planned_start_date || "-"} até ${projeto.initiative_details?.planned_end_date || "-"}\n\n` +
` *Subtarefas Concluídas:* ${projeto.finished_subtask_count ?? 0}\n` +
` *Subtarefas Pendentes:* ${projeto.unfinished_subtask_count ?? 0}\n\n` +
` *Resumo 5W2H:*\n` +
`🔹 *O que?* ${oQue}\n` +
`🔹 *Por que?* ${porQue}\n` +
`🔹 *Onde?* ${onde}\n` +
`🔹 *Quando?* ${quando}\n` +
`🔹 *Quem?* ${quem}\n` +
`🔹 *Como?* ${como}\n` +
`🔹 *Quanto?* ${quanto}\n\n` +
` Deseja consultar outro projeto? Envie o ID ou digite "sair".`;

return [{ json: { mensagem } }];

return [{
  json: {
    etapa: 'aguardando_projeto_id',
    numero: $node["Gerenciar Etapa da Conversa"].json.numero,
    mensagem: '❌ Projeto não encontrado.\n\n🔁 Verifique se o ID informado está correto ou digite "sair" para encerrar.\n\nSe quiser, posso listar os projetos novamente.'
  }
}];

