const projeto = $node["Responder Detalhes do Projeto"].json;
const colunas = [$node["Buscar Colunas do Board"].json.data]; // entre colchetes 👈

const colunaAtual = colunas.find(c => c.column_id === projeto.column_id);

return [{
  json: {
    ...projeto,
    status_atual: colunaAtual?.name || "(não informado)"
  }
}];

const resultados = items.map(item => {
  const dados = item.json || {};
  const mensagem = dados.mensagem || '';
  const status_atual = dados.status_atual || 'Não informado';

  // Limpa os \n para evitar interferência
  const textoLimpo = mensagem.replace(/\\n/g, '\n');

  function extrair(texto, marcador) {
    const regex = new RegExp(`\\*${marcador}:\\*\\s*(.*?)($|\\n)`, 'i');
    const match = texto.match(regex);
    return match ? match[1].trim() : 'Não informado';
  }

  const objetivo = extrair(textoLimpo, "Objetivo");
  const concluidas = extrair(textoLimpo, "Subtarefas Concluídas");
  const pendentes = extrair(textoLimpo, "Subtarefas Pendentes");

  const prompt = `
Considere o seguinte cenário de projeto:

- Objetivo: ${objetivo}
- Status Atual: ${status_atual}
- Atividades Concluídas: ${concluidas}
- Atividades Pendentes: ${pendentes}

Com base nisso, forneça uma visão geral estratégica e clara da situação do projeto, como se fosse um resumo de status para um diretor. Use linguagem profissional, breve e objetiva.
`;

  return { prompt };
});

return resultados;

