const numero = $json.numero || "";

return [
  {
    json: {
      etapa: "aguardando_pergunta_analitica",
      numero,
      mensagem:
        `🤖 Agora estou pronto para te ajudar com a análise dos projetos!\n\n` +
        `Você pode me perguntar, por exemplo:\n` +
        `🔹 "Qual projeto está parado?"\n` +
        `🔹 "O projeto 941 está atrasado?"\n` +
        `🔹 "Quais são os estratégicos?"\n` +
        `🔹 "Me dá um resumo geral"\n\n` +
        `💬 Escreva com suas palavras! Estou pronto pra responder.`
    }
  }
];

const projetos = $items("Buscar Projetos do Board", 0)[0].json.data.data;

const hoje = new Date().toLocaleDateString('pt-BR');

let prompt = `Considere a data de hoje: ${hoje}.\n\n`;
prompt += `Abaixo está a lista de projetos com seus dados brutos:\n\n`;

for (const p of projetos) {
  const concluidas = p.finished_subtask_count || 0;
  const pendentes = p.unfinished_subtask_count || 0;
  const total = concluidas + pendentes;

  prompt += `- Projeto: ${p.title || '(sem título)'}\n`;
  prompt += `  ID: ${p.card_id}\n`;
  prompt += `  Status: ${p.status || p.column?.name || '(indefinido)'}\n`;
  prompt += `  Início: ${p.start_date || 'sem data'}\n`;
  prompt += `  Deadline: ${p.deadline || 'sem prazo'}\n`;
  prompt += `  Tarefas: ${concluidas} concluídas de ${total}\n\n`;
}

prompt += `Com base nesses dados, responda:\n`;
prompt += `1. Qual projeto está mais atrasado e por quê?\n`;
prompt += `2. Quais estão em risco?\n`;
prompt += `3. Quais estão em dia?\n`;

return [{
  json: {
    prompt
  }
}];

const respostaIA = $json.message.content || "❌ Não consegui interpretar a análise no momento.";

return [{
  json: {
    numero: $json.numero || '', // ou resgate de outra etapa anterior
    mensagem: `🧠 *Análise do Assistente de Projetos*\n\n${respostaIA.trim()}\n\nSe precisar de mais informações, é só perguntar!`
  }
}];
